package main

import (
	"encoding/json"
	"flag"
	"fmt"
	"github.com/dchest/captcha"
	"io"
	"log"
	"net/http"
	"os"
	"strconv"
)

type sendData struct {
	PassWord string
	Picture  string
}

const port = 8082

func main() {
	http.HandleFunc("/", senderJson)
	fmt.Printf("Starting server at port " + strconv.Itoa(port) + "\n")
	if err := http.ListenAndServe((":" + strconv.Itoa(port)), nil); err != nil {
		log.Fatal(err)
	}
}

func senderJson(w http.ResponseWriter, r *http.Request) {

	if r.Method != "GET" {
		http.Error(w, "Method is not supported.", http.StatusNotFound)
		return
	}

	passWordString, pic, err := CreateCaptcha2()
	if err != nil {
		http.Error(w, "Server Error", http.StatusInternalServerError)
		return
	}
	//	pic := "data:image/jpeg;base64,"
	//	passWordString := "012345"

	json, err := json.Marshal(sendData{PassWord: passWordString, Picture: pic})
	if err != nil {
		fmt.Println("error:", err)
	}

	fmt.Fprintf(w, string(json))

}

var (
	flagImage = flag.Bool("i", true, "output image captcha")
	flagLen   = flag.Int("len", 6, "length of captcha")
	flagImgW  = flag.Int("width", 199, "image captcha width")
	flagImgH  = flag.Int("height", 150, "image captcha height")
)

func (jr *Job_Result) Write(p []byte) (n int, err error) {
	//	s2 := strings.TrimRight(string(p), "\n ")
	jr.PicB64 = append(jr.PicB64, p)
	return jr.PicB64, nil
}

type Job_Result struct {
	PicB64 []byte
}

func CreateCaptcha2() (string, string, error) {

	f, err := os.OpenFile("01.png", os.O_WRONLY|os.O_CREATE|os.O_APPEND, 0600)
	if err != nil {
		return "", "", err
	}

	//	var b2 io.Writer
	var w io.WriterTo

	d := captcha.RandomDigits(*flagLen)
	w = captcha.NewImage("", d, *flagImgW, *flagImgH)

	jr := new(Job_Result)
	w.WriteTo(jr)
	fmt.Println(jr)

	w.WriteTo(f)

	return string(d), "0123", err
}
